mapper.insert:
INSERT INTO wf_market_item (id, en_name, zh_name, url_name,ducats, use_count) VALUES
(#{item.id}, #{item.enName}, #{item.zhName}, #{item.urlName}, #{item.ducats}, #{item.useCount})
ON DUPLICATE KEY UPDATE en_name = VALUES(en_name), zh_name = VALUES(zh_name), url_name = VALUES(url_name), ducats = VALUES(ducats), use_count = VALUES(use_count);

mapper.select:
SELECT id, en_name, zh_name, url_name, use_count
FROM wf_market_item
WHERE (url_name LIKE CONCAT('%', #{paramsMap.finalQueryString}, '%')
    OR zh_name REGEXP #{paramsMap.finalQueryStringRegex}
    OR en_name LIKE CONCAT('%', #{paramsMap.finalQueryString}, '%')
    OR zh_name REGEXP #{paramsMap.zhRegex}
    OR en_name LIKE CONCAT('%', #{paramsMap.zh}, '%')
    OR url_name LIKE CONCAT('%', #{paramsMap.zh}, '%')
          );
